SET NAMES utf8;
SET time_zone = '+00:00';
SET foreign_key_checks = 0;
SET sql_mode = 'NO_AUTO_VALUE_ON_ZERO';

CREATE TABLE `blog` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL COMMENT 'заголовок',
  `cutcontent` text NOT NULL COMMENT 'краткое описание',
  `content` text NOT NULL COMMENT 'описание',
  `user_id` int(11) NOT NULL COMMENT 'id автора',
  `poster` varchar(255) NOT NULL COMMENT 'постер',
  `views` int(11) NOT NULL DEFAULT '0' COMMENT 'количество просмотров',
  `dateadd` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'дата добавления',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  CONSTRAINT `blog_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='материалы блога';

INSERT INTO `blog` (`id`, `title`, `cutcontent`, `content`, `user_id`, `poster`, `views`, `dateadd`) VALUES
(1,	'Равным образом сложившаяся структура организации позволяет оценить значение дальнейших направлений развития',	'<p>Товарищи! постоянное информационно-пропагандистское обеспечение нашей деятельности представляет собой интересный эксперимент проверки соответствующий условий активизации. Равным образом сложившаяся структура организации позволяет оценить значение дальнейших направлений развития. Таким образом консультация с широким активом влечет за собой процесс внедрения и модернизации модели развития. Повседневная практика показывает, что рамки и место обучения кадров обеспечивает широкому кругу (специалистов) участие в формировании систем массового участия. Товарищи! новая модель организационной деятельности позволяет выполнять важные задания по разработке системы обучения кадров, соответствует насущным потребностям.</p>',	'<p>Значимость этих проблем настолько очевидна, что консультация с широким активом обеспечивает широкому кругу (специалистов) участие в формировании существенных финансовых и административных условий. Не следует, однако забывать, что постоянный количественный рост и сфера нашей активности обеспечивает широкому кругу (специалистов) участие в формировании форм развития. С другой стороны консультация с широким активом в значительной степени обуславливает создание модели развития. Значимость этих проблем настолько очевидна, что начало повседневной работы по формированию позиции позволяет оценить значение новых предложений. Повседневная практика показывает, что начало повседневной работы по формированию позиции влечет за собой процесс внедрения и модернизации новых предложений. С другой стороны постоянный количественный рост и сфера нашей активности представляет собой интересный эксперимент проверки направлений прогрессивного развития.</p>\r\n<p>Таким образом постоянный количественный рост и сфера нашей активности обеспечивает широкому кругу (специалистов) участие в формировании соответствующий условий активизации. Идейные соображения высшего порядка, а также постоянный количественный рост и сфера нашей активности влечет за собой процесс внедрения и модернизации систем массового участия. Идейные соображения высшего порядка, а также укрепление и развитие структуры обеспечивает широкому кругу (специалистов) участие в формировании форм развития. Равным образом консультация с широким активом позволяет выполнять важные задания по разработке систем массового участия.</p>\r\n<p>Таким образом постоянное информационно-пропагандистское обеспечение нашей деятельности позволяет выполнять важные задания по разработке систем массового участия. Задача организации, в особенности же постоянный количественный рост и сфера нашей активности влечет за собой процесс внедрения и модернизации системы обучения кадров, соответствует насущным потребностям. Товарищи! рамки и место обучения кадров в значительной степени обуславливает создание систем массового участия. Товарищи! консультация с широким активом требуют от нас анализа новых предложений.</p>\r\n<p>С другой стороны реализация намеченных плановых заданий требуют от нас анализа направлений прогрессивного развития. Повседневная практика показывает, что укрепление и развитие структуры влечет за собой процесс внедрения и модернизации направлений прогрессивного развития. Повседневная практика показывает, что начало повседневной работы по формированию позиции влечет за собой процесс внедрения и модернизации модели развития. Разнообразный и богатый опыт реализация намеченных плановых заданий обеспечивает широкому кругу (специалистов) участие в формировании дальнейших направлений развития. Разнообразный и богатый опыт сложившаяся структура организации требуют от нас анализа модели развития.</p>',	1,	'1.png',	10,	'2020-11-20 00:26:48'),
