CREATE TABLE `blog_comments` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `blog_id` int(11) NOT NULL COMMENT 'id записи блога',
  `user_id` int(11) NOT NULL COMMENT 'id автора',
  `content` text NOT NULL COMMENT 'текст материала',
  `dateadd` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'дата добавления',
  PRIMARY KEY (`id`),
  KEY `blog_id` (`blog_id`),
  KEY `user_id` (`user_id`),
  CONSTRAINT `blog_comments_ibfk_2` FOREIGN KEY (`blog_id`) REFERENCES `blog` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `blog_comments_ibfk_4` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='комментарии блога';

